definition.script:
node("master"){
    stage("Clean workspace"){
        cleanWs()
    }
    stage("Consul Deployment") {
     sh "git clone https://github.com/hashicorp/consul-helm.git"
     sh "export KUBECONFIG=/var/jenkins_home/kubeconfig && kubectl create ns consul && helm install consul ./consul-helm --namespace consul && kubectl get all -n consul"
    }
    stage("Deploy Vault") {
        sh "export KUBECONFIG=/var/jenkins_home/kubeconfig && kubectl create ns vault && helm repo add hashicorp https://helm.releases.hashicorp.com && helm repo update && helm install vault hashicorp/vault --namespace vault && kubectl get all -n vault"
    }
    stage("Deploy Sonarqube") {
        sh "export KUBECONFIG=/var/jenkins_home/kubeconfig && helm repo add sonarqube https://SonarSource.github.io/helm-chart-sonarqube && helm repo update && kubectl create namespace sonarqube && helm upgrade --install -n sonarqube sonarqube sonarqube/sonarqube && kubectl get all -n sonarqube"
    }
    stage("Deploy Nexus") {
        sh """
           cat << EOF > deployment.yaml
    apiVersion: apps/v1
    kind: Deployment
    metadata:
      name: nexus
      namespace: nexus
    spec:
      replicas: 1
      selector:
        matchLabels:
            app: nexus-server
      template:
        metadata:
          labels:
            app: nexus-server
        spec:
          containers:
            - name: nexus
              image: sonatype/nexus3:latest
              resources:
                limits:
                  memory: "1Gi"
                  cpu: "1000m"
                requests:
                  memory: "1Gi"
                  cpu: "500m"
              ports:
                - containerPort: 8081
              volumeMounts:
                - name: nexus-data
                  mountPath: /nexus-data
          volumes:
            - name: nexus-data
              emptyDir: {}

EOF


        """
        sh "export KUBECONFIG=/var/jenkins_home/kubeconfig  && kubectl create namespace nexus && kubectl create -f deployment.yaml && kubectl get all -n nexus"
    }
    stage("Deploy Jenkins") {
        sh """
         export KUBECONFIG=/var/jenkins_home/kubeconfig && kubectl create ns jenkins && helm repo add bitnami https://charts.bitnami.com/bitnami && helm install jenkins bitnami/jenkins -n jenkins && kubectl get all -n jenkins
        """
    }
    stage("Deploy Prometheus"){
        sh "export KUBECONFIG=/var/jenkins_home/kubeconfig  && kubectl create namespace monitoring && helm repo add prometheus-community https://prometheus-community.github.io/helm-charts && helm repo add kube-state-metrics https://kubernetes.github.io/kube-state-metrics && helm repo update && helm install prometheus prometheus-community/prometheus -n monitoring && kubectl get all -n monitoring"
    }
    stage("Deploy Grafana") {
        sh "export KUBECONFIG=/var/jenkins_home/kubeconfig  && helm repo add bitnami https://charts.bitnami.com/bitnami && helm install grafana bitnami/grafana -n monitoring && kubectl get all -n monitoring"
    }
    stage("Deploy Jaeger"){
        sh "export KUBECONFIG=/var/jenkins_home/kubeconfig && kubectl create ns jaeger && helm repo add jaegertracing https://jaegertracing.github.io/helm-charts && helm install jaeger jaegertracing/jaeger -n jaeger"
    }
    stage("Display All resource"){
        sh "export KUBECONFIG=/var/jenkins_home/kubeconfig && kubectl get all --all-namespaces"
    }
}
